CASE [fixed DTM]

WHEN "0" THEN { FIXED [RpDate],[R/Rr (non repos)]:Sum(IF [Maturity Date]>[date series] AND [Start date]<=[date series] AND [Source]<>"EUROCLEAR" AND [R/Rr (non repos)]="Repo" AND INT([Is Internal ?])<>1 THEN ([Initial Cash USD T]) ELSE 0 END)}
WHEN "1" THEN { FIXED [RpDate]:Sum(IF [Maturity Date]>[date series] AND [Start date]<=[date series] AND [Source]<>"EUROCLEAR" AND [R/Rr (non repos)]="Repo" AND INT([Is Internal ?])<>1 THEN ([Initial Cash USD T]) ELSE 0 END)}
WHEN "2" THEN { FIXED [RpDate]:Sum(IF [Maturity Date]>[date series] AND [Start date]<=[date series]AND [Source]<>"EUROCLEAR" AND [R/Rr (non repos)]="Repo" AND [Is Internal ?]<>"1" THEN ([Initial Cash USD T]) ELSE 0 END)}
WHEN "3" THEN { FIXED [RpDate]:Sum(IF INT([DTM T ])>= 3 AND [Source]<>"EUROCLEAR" AND [R/Rr (non repos)]<>"non" THEN ([Initial Cash USD T (excl Int/FWD)]) ELSE 0 END)}
WHEN "4" THEN { FIXED [RpDate]:Sum(IF INT([DTM T ])>= 4 AND [Source]<>"EUROCLEAR" AND [R/Rr (non repos)]<>"non" THEN ([Initial Cash USD T (excl Int/FWD)]) ELSE 0 END)}
WHEN "5" THEN { FIXED [RpDate]:Sum(IF INT([DTM T ])>= 5 AND [Source]<>"EUROCLEAR" AND [R/Rr (non repos)]<>"non" THEN ([Initial Cash USD T (excl Int/FWD)]) ELSE 0 END)}


ELSE 0 

END

