fixes.dax_expression:
VAR td = 'migration_Extract_POC (1) (local copy)'[title date]
VAR mat = 'migration_Extract_POC (1) (local copy)'[Maturity Date]
VAR holidays = SELECTEDVALUE('Holidays'[Display As])
RETURN
IF(
    WEEKDAY(td) = 6,
    IF(
        td + 3 = holidays,
        IF(
            mat <= td, "expired",
            IF(mat <= td + 4, "O/n",
                IF(mat <= td + 7, "1W",
                    IF(mat <= td + 14, "2W",
                        IF(mat <= EDATE(td, 1), "1M",
                            IF(mat <= td + 96, "3M",
                                IF(mat <= td + 189, "6M",
                                    IF(mat <= td + 371, "1 Year",
                                        IF(mat >= td + 371, "> 1 Year", BLANK())
                                    )
                                )
                            )
                        )
                    )
                )
            )
        ),
        IF(
            mat <= td, "expired",
            IF(mat <= td + 3, "O/n",
                IF(mat <= td + 7, "1W",
                    IF(mat <= td + 14, "2W",
                        IF(mat <= EDATE(td, 1), "1M",
                            IF(mat <= td + 96, "3M",
                                IF(mat <= td + 189, "6M",
                                    IF(mat <= td + 371, "1 Year",
                                        IF(mat >= td + 371, "> 1 Year", BLANK())
                                    )
                                )
                            )
                        )
                    )
                )
            )
        )
    ),
    IF(
        td + 3 = holidays,
        IF(mat <= td + 2, "O/n",
            IF(mat <= td + 7, "1W",
                IF(mat <= td + 14, "2W",
                    IF(mat <= EDATE(td, 1), "1M",
                        IF(mat <= td + 96, "3M",
                            IF(mat <= td + 189, "6M",
                                IF(mat <= td + 371, "1 Year",
                                    IF(mat >= td + 371, "> 1 Year", BLANK())
                                )
                            )
                        )
                    )
                )
            )
        ),
        IF(mat <= td + 1, "O/n",
            IF(mat <= td + 7, "1W",
                IF(mat <= td + 14, "2W",
                    IF(mat <= EDATE(td, 1), "1M",
                        IF(mat <= td + 96, "3M",
                            IF(mat <= td + 189, "6M",
                                IF(mat <= td + 371, "1 Year",
                                    IF(mat >= td + 371, "> 1 Year", BLANK())
                                )
                            )
                        )
                    )
                )
            )
        )
    )
)
